import React from 'react';

const Game = ({allCards, shuffleDeck}) => {

    function firstHand(cards){
        const deck = shuffleDeck(cards);
        const fiveCards = 5;
        const hand = deck.slice(0, fiveCards);
        const showHand = hand.map((card) => {
            return <li>card.name</li>
        });
    };

    return(
        <>
        <p>This is the player game</p>
        {firstHand(allCards)}
        </>
    );

};

export default Game;
