import React, {useState} from 'react';

const InitialDeck = ({allCards}) => {

    let randomDeck = [];
    for (let card of allCards){
        let randomIndex = Math.floor(Math.random() * allCards.length);
        while(randomDeck.includes(allCards[randomIndex])){
            randomIndex = Math.floor(Math.random() * allCards.length);
        }
        randomDeck[allCards] = allCards[randomIndex];
    }

    return(
        <>
        <p>Initial Deck</p>
        {console.log(randomDeck)}
        </>
    );
};

export default InitialDeck;
