import React from 'react';
import './game.css';

const Game = ({allCards, firstHand}) => {

    const playerCards = firstHand(allCards).map((card) => {
        return(
            <div className="player-hand">
                <div className="player-card" key={card._id}><img src={card.img} alt={card.name} width="100"/></div>
            </div>
        )
    });

    return(
        <div>
            <p>This is your hand</p>
                {playerCards}
        </div>
    );

};

export default Game;
