import React from 'react';

const Game = ({allCards, shuffleDeck}) => {

    shuffleDeck(allCards);

    function firstHand(){
        const deck = shuffleDeck(allCards);
        const fiveCards = 5;
        const hand = deck.slice(0, fiveCards);
        console.log(hand);
    }

    return(
        <>
        <p>This is the player game</p>
        {firstHand()}
        </>
    );

};

export default Game;
