import React, {useState} from 'react';

const InitialDeck = ({allCards}) => {

    const deck = [];
    for (const card of allCards){
        notInOrder = deck.push(card);
        notInOrder.sort(() => Math.random() - 0.5)
    }

    return(
        <p>Initial Deck</p>
    );
};

export default InitialDeck;
