import React from 'react';

const Game = ({allCards, firstHand}) => {

    firstHand = (cards) => {
        const deck = shuffleDeck(cards);
        const fiveCards = 5;
        const hand = deck.slice(0, fiveCards);
        return hand;
    };

    const cardNodes = allCards.map((card, index) => {
        return <li key={index}>{card.name}</li>
    });

    return(
        <div>
            <p>This is your hand</p>
            <ul>
                {cardNodes}
            </ul>
        </div>
    );

};

export default Game;
