import React, {useState} from 'react';

const InitialDeck = ({allCards}) => {

    function shuffle(allCards){
        for (const card = allCards.length-1; card>0; card--){
            const j = Math.floor(Math.random() * (card+1));
            const temp = allCards[card];
            allCards[card] = allCards[j];
            allCards[j] = temp;
        }
    }

    return(
        <>
        <p>Initial Deck</p>
        </>
    );
};

export default InitialDeck;
