import React from 'react';

const Game = ({allCards, firstHand}) => {

    const playerCards = firstHand(allCards).map((card, index) => {
        return <div key={index}><img src={card.img} alt={card.name}/></div>
    });

    return(
        <div>
            <p>This is your hand</p>
                {playerCards}
        </div>
    );

};

export default Game;
