import React from 'react';

const Player1Materials = ({playedCard, player1Materials, onMaterialCardClick}) => {

     const handleChange = function(event){
          const cardNames = player1Materials.map(card => card.name)
          if (cardNames.includes(playedCard.name)){
               return 
          }else{
               onMaterialCardClick(player1Materials[event.target.value])
          }
     };

     const player1Cards = player1Materials.map((card) => {
          return <li className="player-card" key={card._id}><img src={card.img} alt={card.name}/></li>
      });  

     return(
          <p onChange={handleChange}>{player1Cards}</p>
     );

};

export default Player1Materials;
