import React from 'react';

const Game = ({allCards, shuffleDeck, firstHand}) => {

    let initDeck = shuffleDeck(allCards);
    let playerHand = firstHand(initDeck);
    console.log(playerHand)

    let cardNodes = playerHand.map((card, index) => {
        return <li key={index}>{card.name}</li>
    });

    return(
        <div>
            <p>This is the player game</p>
            <ul>
                {cardNodes}
            </ul>
        </div>
    );

};

export default Game;
