import React from 'react';

const Game = ({allCards, shuffleDeck, firstHand}) => {

    let shuffle = shuffleDeck(allCards);
    let hand = firstHand(shuffle);

    const playerHand = hand.map((card, index) => {
        return <li key={index}>{card.name}</li>
    })

    return(
        <>
        <p>This is the player game</p>
        {playerHand}
        </>
    );

};

export default Game;
