import React from 'react';

const Game = ({allCards, shuffleDeck}) => {

    function firstHand(){
        const deck = shuffleDeck(allCards);
        const fiveCards = 5;
        const hand = deck.slice(0, fiveCards);
        return hand
    }


    return(
        <p>This is the player game</p>
    );

};

export default Game;
