import React from 'react';

const Player1Hand = ({player1Hand}) => {

    const handleClickPlay = function(card){
        onMaterialCardClick(card)
    };

    const player1Cards = player1Hand.map((card) => {
        return <li className="player-card" key={card._id}><img src={card.img} alt={card.name}/><button onClick="handleClickPlay" type="play">Play</button></li>
    });

    return(
        <>
        {player1Cards}
        </>
        )
};

export default Player1Hand;
