import React, {useState} from 'react';

const InitialDeck = ({allCards}) => {

    const shuffleCards = function(){
        return allCards.sort(() => Math.random - 0.5);
    }

    return(
        <>
        <p>Initial Deck</p>
        {shuffleCards()}
        </>
    );
};

export default InitialDeck;
