import React, {useState} from 'react';

const InitialDeck = ({allCards}) => {

    function shuffleArray(allCards) {
        let curId = allCards.length;
        while (0 !== curId) {
          let randId = Math.floor(Math.random() * curId);
          curId -= 1;
          let tmp = allCards[curId];
          allCards[curId] = allCards[randId];
          allCards[randId] = tmp;
        }
    }

    return(
        <>
        <p>Initial Deck</p>
        {shuffleArray(allCards)}
        </>
    );
};

export default InitialDeck;
